DROP TABLE IF EXISTS #Schredded
go
DECLARE @Num VARCHAR(1000),
	@Len INT
    
SET @Num =''
SELECT @Len = LEN(@Num)

;WITH a AS
(
	SELECT 
		ROW_NUMBER() OVER (ORDER BY object_id) AS NUM
	FROM sys.all_objects
)

SELECT 
	a.NUM AS ID,
	CAST(SUBSTRING(@Num, a.NUM, 1) AS BIGINT) AS SubNum
INTO #Schredded
FROM a
WHERE NUM <= @Len


SELECT TOP 1 *
FROM
(
	SELECT 
		a.SubNum *     b.SubNum *     c.SubNum *     d.SubNum *     e.SubNum *     f.SubNum *     g.SubNum *     h.SubNum *     i.SubNum *     j.SubNum *     k.SubNum *     l.SubNum * m.SubNum AS Product
	FROM #Schredded a
		INNER JOIN #Schredded b
			ON a.id = b.id + 1
		INNER JOIN #Schredded c
			ON a.id = c.id + 2
		INNER JOIN #Schredded d
			ON a.id = d.id + 3
		INNER JOIN #Schredded e
			ON a.id = e.id + 4
		INNER JOIN #Schredded f
			ON a.id = f.id + 5
		INNER JOIN #Schredded g
			ON a.id = g.id + 6
		INNER JOIN #Schredded h
			ON a.id = h.id + 7
		INNER JOIN #Schredded i
			ON a.id = i.id + 8
		INNER JOIN #Schredded j
			ON a.id = j.id + 9
		INNER JOIN #Schredded k
			ON a.id = k.id + 10
		INNER JOIN #Schredded l
			ON a.id = l.id + 11
		INNER JOIN #Schredded m
			ON a.id = m.id + 12
) prd
ORDER BY prd.Product DESC
